        if (row.item_code && row.custom_cantidad_m2) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function(r) {
                    if (r.message && r.message.uoms) {
                        let uom_m2 = r.message.uoms.find(u => u.uom === "m2");
                        if (uom_m2 && uom_m2.conversion_factor) {
                            let cajas = row.custom_cantidad_m2 / uom_m2.conversion_factor;
                            if (!Number.isInteger(cajas)) {
                                let dialog = new frappe.ui.Dialog({
                                    title: 'Redondeo de cantidad de cajas',
                                    fields: [
                                        {
                                            fieldtype: 'HTML',
                                            fieldname: 'info',
                                            options: `
                                                <div>
                                                    La cantidad ingresada en m² no equivale a un número entero de cajas.<br><br>
                                                    <b>Opción 1:</b> Redondear hacia abajo (<b>${Math.floor(cajas)}</b> cajas, <b>${(Math.floor(cajas) * uom_m2.conversion_factor).toFixed(2)}</b> m²)<br>
                                                    <b>Opción 2:</b> Redondear hacia arriba (<b>${Math.ceil(cajas)}</b> cajas, <b>${(Math.ceil(cajas) * uom_m2.conversion_factor).toFixed(2)}</b> m²)<br><br>
