                                        frappe.model.set_value(cdt, cdn, 'custom_cantidad_m2', Math.ceil(cajas) * uom_m2.conversion_factor);
                                        frm.refresh_field('items');
                                        dialog.hide();
                                    },
                                    secondary_action_label: '1',
                                    secondary_action: function() {
                                        frappe.model.set_value(cdt, cdn, 'qty', Math.floor(cajas));
                                        frappe.model.set_value(cdt, cdn, 'custom_cantidad_m2', Math.floor(cajas) * uom_m2.conversion_factor);
                                        frm.refresh_field('items');
                                        dialog.hide();
