frappe.ui.form.on('Price List', {
    refresh(frm) {
        frm.add_custom_button('Actualizador masivo de precios', () => {
            frappe.prompt([
                {
                    fieldname: 'porcentaje',
                    label: 'Porcentaje de incremento (%)',
                    fieldtype: 'Float',
                    reqd: 1
                }
            ], (values) => {
                frappe.call({
                    method: 'mdx_lcdp.api.actualizar_precios_masivo',
                    args: {
                        price_list: frm.doc.name,
                        porcentaje: values.porcentaje
                    },
                    callback: function(r) {
                        frappe.msgprint({
                            title: 'Resultado de la actualización',
                            indicator: r.message.includes('exitosa') ? 'green' : 'red',
                            message: r.message,
                            wide: true
                        });
                        frm.reload_doc();
                    }
                });
            }, 'Actualizar precios', 'Actualizar');
        });
    }
});

frappe.ui.form.on('Quotation', {
    onload: function(frm) {
        if (!frm.doc.valid_till) {
            let today = frappe.datetime.get_today();
            let two_days_later = frappe.datetime.add_days(today, 2);
            frm.set_value('valid_till', two_days_later);
        }
    }
});
