script:
frappe.ui.form.on('Quotation Item', {
    qty: function(frm, cdt, cdn) {
        calcular_m2(frm, cdt, cdn);
    },
    item_code: function(frm, cdt, cdn) {
        calcular_m2(frm, cdt, cdn);
    },
    custom_cantidad_m2: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.item_code && row.custom_cantidad_m2) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function(r) {
                    if (r.message && r.message.uoms) {
                        let uom_m2 = r.message.uoms.find(u => u.uom === "m2");
                        if (uom_m2 && uom_m2.conversion_factor) {
                            let cajas = row.custom_cantidad_m2 / uom_m2.conversion_factor;
                            if (!Number.isInteger(cajas)) {
                                let dialog = new frappe.ui.Dialog({
                                    title: 'Redondeo de cantidad de cajas',
                                    fields: [
                                        {
                                            fieldtype: 'HTML',
                                            fieldname: 'info',
                                            options: `
                                                <div>
                                                    La cantidad ingresada en m² no equivale a un número entero de cajas.<br><br>
                                                    <b>Opción 1:</b> Redondear hacia abajo (<b>${Math.floor(cajas)}</b> cajas, <b>${(Math.floor(cajas) * uom_m2.conversion_factor).toFixed(2)}</b> m²)<br>
                                                    <b>Opción 2:</b> Redondear hacia arriba (<b>${Math.ceil(cajas)}</b> cajas, <b>${(Math.ceil(cajas) * uom_m2.conversion_factor).toFixed(2)}</b> m²)<br><br>
                                                    Por favor, elige una opción:
                                                </div>
                                            `
                                        }
                                    ],
                                    primary_action_label: '2',
                                    primary_action: function() {
                                        row.qty = Math.ceil(cajas);
                                        row.custom_cantidad_m2 = Math.ceil(cajas) * uom_m2.conversion_factor;
                                        frm.refresh_field('items');
                                        dialog.hide();
                                    },
                                    secondary_action_label: '1',
                                    secondary_action: function() {
                                        row.qty = Math.floor(cajas);
                                        row.custom_cantidad_m2 = Math.floor(cajas) * uom_m2.conversion_factor;
                                        frm.refresh_field('items');
                                        dialog.hide();
                                    }
                                });
                                dialog.show();

                                // Personaliza el color de ambos botones
                                setTimeout(function() {
                                    let buttons = dialog.$wrapper.find('.modal-footer button');
                                    buttons.css({
                                        'background': '#212121',
                                        'color': '#fff',
                                        'border': 'none'
                                    });
                                }, 100);
                            } else {
                                row.qty = cajas;
                                row.custom_cantidad_m2 = cajas * uom_m2.conversion_factor;
                                frm.refresh_field('items');
                            }
                        }
                    }
                }
            });
        }
    }
});

function calcular_m2(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    if (row.item_code && row.qty) {
        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Item",
                name: row.item_code
            },
            callback: function(r) {
                if (r.message && r.message.uoms) {
                    let uom_m2 = r.message.uoms.find(u => u.uom === "m2");
                    if (uom_m2 && uom_m2.conversion_factor) {
                        row.custom_cantidad_m2 = row.qty * uom_m2.conversion_factor;
                        frm.refresh_field('items');
                    }
                }
            }
        });
    }
}
